import React, { useEffect, useState } from "react";
import { fetchSuggestions } from "./utils/api";
import "./Autocomplete.css";
import ProductDetail from "./ProductDetail";

function Autocomplete() {
  const [searchTerm, setSearchTerm] = useState("");
  const [Id, setId] = useState();
  const [suggestions, setSuggestions] = useState([]);
  const [isLoading, setIsLoading] = useState(false)

  useEffect(() => {
    setTimeout(() => {  
      if(searchTerm !== "" || searchTerm != null){  
      setIsLoading(true)   
      fetchSuggestions(searchTerm).then((_suggestions) => {
        let bucket = []
        for(let i = 0; i < 10; i++){
          if(_suggestions[i] != null || _suggestions[i] !== undefined){
            bucket[i] = _suggestions[i]
          }
        }
        setSuggestions(bucket)
        setIsLoading(false)      
    }
      )
        };
    }, 10500)

  }, [searchTerm]);

  console.log(suggestions)
  console.log(isLoading)

  return (
    <div className="search-container">
      {isLoading && searchTerm ? 
       <div className="product-row">
        {Id ? <ProductDetail productId={Id} /> : ""}
      </div>
      : " Loading..."}
      <div className="search-row">
      <input
        type="text"
        value={searchTerm}
        className="search-box"
        placeholder="Search for a product"
        onChange={(e) => setSearchTerm(e.target.value)}
      />
       {searchTerm ? suggestions?.map((item, key) => {
          return (
            <div className="autocomplete-items">
              <button onClick={() => {
                setId(item?.id)
                setSearchTerm("")
                setIsLoading(true)
              }}>{item?.title}</button>
            </div>
          )
      }) : ""}
      </div>
    </div>
  );
}

export default Autocomplete;
